mapper.select:
SELECT id,name,level,project_id projectId ,parent_id parentId FROM sm_task



        and id=#{taskId}


        and project_id=#{projectId}

SELECT id,name,level,project_id projectId ,parent_id parentId FROM sm_task



        and project_id=#{projectId} and level = 1


        and parent_id=#{parentId} and level =#{level}

SELECT complete_rate percentage, CONCAT_WS('/',LEFT ( t.plan_start_time, 10 ),LEFT ( t.plan_end_time, 10 )) planCycle, t.principal FROM sm_task t INNER JOIN sm_task_comment c ON t.id=c.task_id
AND t.project_id=#{projectId}
AND c.task_id=#{taskId}

SELECT

FROM sm_task t INNER JOIN sm_task_comment c ON t.id=c.task_id


        AND t.project_id=#{projectId}


        AND c.task_id=#{taskId}


        AND t.principal LIKE CONCAT('%',#{principal},'%')


        AND t.update_time BETWEEN #{updateTime} AND NOW()

mapper.update:
UPDATE sm_task
update_time=now(), name=#{taskName}, principal=#{principal}, priority=#{priority}, stage=#{stage}, time_sheet=#{timeSheet}, workload=#{workload}, description=#{description}, percentage=#{percentage}, plan_start_time=#{planStartTime}, plan_end_time=#{planEndTime},
WHERE id=#{id}
