mapper.select:
SELECT id,name,level,project_id projectId ,parent_id parentId FROM sm_task



        and id=#{taskId}


        and project_id=#{projectId}

SELECT id,name,level,project_id projectId ,parent_id parentId FROM sm_task



        and project_id=#{projectId}


        and parent_id=#{parentId}


        and level =#{level}

SELECT id,name,level,parent_id parentId FROM sm_timesheet



        and parent_id=#{parentId}


        and level =#{level}

SELECT c.id, complete_rate percentage, CONCAT_WS('/',SUBSTRING( t.plan_start_time, 6,5 ),SUBSTRING( t.plan_end_time, 6,5 )) planCycle, t.principal FROM sm_task t INNER JOIN sm_task_comment c ON t.id=c.task_id
AND t.project_id=#{projectId} AND t.id=#{id}
AND c.task_id=#{taskId}

mapper.update:
UPDATE sm_task
update_time=now(), name=#{taskName}, principal=#{principal}, priority=#{priority}, stage=#{stage}, time_sheet=#{timeSheet}, workload=#{workload}, description=#{description}, percentage=#{percentage}, plan_start_time=#{planStartTime}, plan_end_time=#{planEndTime},
WHERE id=#{id}

UPDATE sm_timesheet
update_time=now(), name=#{laborName}, labor_hour=#{laborHour},
WHERE id=#{id}
